import React from 'react';
import { View, Text, Image, TouchableOpacity } from 'react-native';
import styles from './styles';

const EventBox = ({ title, description, publisher, likes, comments }) => {
  return (
    <TouchableOpacity style={styles.boxContainer}>
      <View style={styles.profileContainer}>
        <Image source={{ uri: publisher.image }} style={styles.profileImage} />
        <Text style={styles.publisherName}>{publisher.name}</Text>
      </View>
      <View style={styles.eventContainer}>
        <Image source={{ uri: 'https://picsum.photos/200/300' }} style={styles.eventImage} />
        <View style={styles.eventDetails}>
          <Text style={styles.eventTitle}>{title}</Text>
          <Text style={styles.eventDescription}>{description}</Text>
        </View>
        <View style={styles.likeCommentContainer}>
          <TouchableOpacity style={styles.likeButton}>
            <Text style={styles.buttonText}>Like ({likes})</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.commentButton}>
            <Text style={styles.buttonText}>Comment ({comments})</Text>
          </TouchableOpacity>
        </View>
      </View>
    </TouchableOpacity>
  );
};

const EventsScreen = () => {
  const events = [
    {
      title: 'Event 1',
      description: 'This is event 1',
      publisher: {
        name: 'John Doe',
        image: 'https://randomuser.me/api/portraits/men/1.jpg',
      },
      likes: 10,
      comments: 5,
    },
    {
      title: 'Event 2',
      description: 'This is event 2',
      publisher: {
        name: 'Jane Doe',
        image: 'https://randomuser.me/api/portraits/women/1.jpg',
      },
      likes: 20,
      comments: 8,
    },
  ];

  return (
    <View style={styles.container}>
      {events.map((event, index) => (
        <EventBox
          key={index}
          title={event.title}
          description={event.description}
          publisher={event.publisher}
          likes={event.likes}
          comments={event.comments}
        />
      ))}
    </View>
  );
};

export default EventsScreen;
