import React from 'react';
import { View, Text, Image, TouchableOpacity, StyleSheet } from 'react-native';

const ProfileScreen = () => {
  const user = {
    name: 'John Doe',
    profileImage: 'https://randomuser.me/api/portraits/men/1.jpg',
    events: [
      {
        title: 'Event 1',
        description: 'This is event 1',
        likes: 10,
        comments: 5,
      },
      {
        title: 'Event 2',
        description: 'This is event 2',
        likes: 20,
        comments: 8,
      },
    ]
  };

  return (
    <View style={styles.container}>
      <View style={styles.profileContainer}>
        <Image source={{ uri: user.profileImage }} style={styles.profileImage} />
        <Text style={styles.username}>{user.name}</Text>
        <TouchableOpacity style={styles.messageButton}>
          <Text style={styles.buttonText}>Message</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.eventsContainer}>
        <Text style={styles.eventsTitle}>Events Created</Text>
        {user.events.map((event, index) => (
          <View style={styles.eventContainer} key={index}>
            <Text style={styles.eventTitle}>{event.title}</Text>
            <Text style={styles.eventDescription}>{event.description}</Text>
            <Text style={styles.eventLikes}>{event.likes} likes</Text>
            <Text style={styles.eventComments}>{event.comments} comments</Text>
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  profileContainer: {
    alignItems: 'center',
    marginTop: 20,
  },
  profileImage: {
    width: 150,
    height: 150,
    borderRadius: 75,
  },
  username: {
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 10,
  },
  messageButton: {
    backgroundColor: '#3f51b5',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 20,
    marginTop: 10,
  },
  buttonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  eventsContainer: {
    marginTop: 20,
  },
  eventsTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
    marginLeft: 20,
  },
  eventContainer: {
    backgroundColor: '#f5f5f5',
    borderRadius: 10,
    padding: 10,
    marginHorizontal: 20,
    marginBottom: 10,
  },
  eventTitle: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  eventDescription: {
    fontSize: 16,
    marginTop: 5,
  },
  eventLikes: {
    fontSize: 14,
    color: 'gray',
    marginTop: 5,
  },
  eventComments: {
    fontSize: 14,
    color: 'gray',
    marginTop: 5,
  },
});

export default ProfileScreen;
